function __(a){if(typeof(i18n)!="undefined"&&i18n[a]){return i18n[a]}return a}function sprintf(d){var e=d.split("%");var a=e[0];var c=/^([ds])(.*)$/;for(var b=1;b<e.length;b++){p=c.exec(e[b]);if(!p||arguments[b]==null){continue}if(p[1]=="d"){a+=parseInt(arguments[b],10)}else{if(p[1]=="s"){a+=arguments[b]}}a+=p[2]}return a}function getValue(b){var a=parseInt(document.getElementById(b).value,10);if(!a||a<0){return 0}return a}function pretty_num(b){b+="";var a="";for(i=b.length;i>3;i-=3){a=i18n.thousands_sep+b.slice(i-3,i)+a}a=b.slice(0,i)+a;return a}function pluralise(a,b,c){if(c!=1){return __(b)}return __(a)};
